import React from 'react';
import logo from './logo.svg';
import './App.css';
import './styles.css';
import { Objectron, ObjectronConfig , ResultsListener, ObjectronInterface} from '@mediapipe/objectron';
import { Camera } from '@mediapipe/camera_utils';
import DeviceDetector from "device-detector-js";



// Usage: testSupport({client?: string, os?: string}[])
// Client and os are regular expressions.
// See: https://cdn.jsdelivr.net/npm/device-detector-js@2.2.10/README.md for
// legal values for client and os
testSupport([
  {client: 'Chrome'},
]);

function testSupport(supportedDevices:{client?: string; os?: string;}[]) {
  const deviceDetector = new DeviceDetector();
  const detectedDevice = deviceDetector.parse(navigator.userAgent);

  // const detectedDevice_ = deviceDetector.parse(navigator.permissions.query({}));
  // navigator.permissions.query({ name: "camera" }).then(res => {
  //   if(res.state == "granted"){
  //       // has permission
  //   }
  // });
  // navigator.permissions.query({ name: 'camera' })

  let isSupported = false;
  for (const device of supportedDevices) {
    if (device.client !== undefined) {
      const re = new RegExp(`^${device.client}$`);
      if (!re.test(detectedDevice.client.name)) {
        continue;
      }
    }
    if (device.os !== undefined) {
      const re = new RegExp(`^${device.os}$`);
      if (!re.test(detectedDevice.os.name)) {
        continue;
      }
    }
    isSupported = true;
    break;
  }
  if (!isSupported) {
    console.log("Bypass device not support");
    // alert(`This demo, running on ${detectedDevice.client.name}/${detectedDevice.os.name}, ` +
    //       `is not well supported at this time, continue at your own risk.`);
  }
}


// const fpsControl = new controls.FPS();


function App() {




  const controls = window;
  const drawingUtils = window;
  const mpObjectron = window;

  // const config:ObjectronConfig = {locateFile: (file: File) => {
  //   return `https://cdn.jsdelivr.net/npm/@mediapipe/objectron@0.3.1627447724/${file}`;
  // }};



  const videoElement =
    document.getElementsByClassName('input_video')[0] as HTMLVideoElement;
  const canvasElement =
    document.getElementsByClassName('output_canvas')[0] as HTMLCanvasElement;
  const controlsElement =
    document.getElementsByClassName('control-panel')[0] as HTMLDivElement;
  const canvasCtx = canvasElement.getContext('2d')!;



    // function onResults(results: mpObjectron.Results): void {
    //   // Hide the spinner.
    //   document.body.classList.add('loaded');

    //   console.log(results.image);
    //   // Update the frame rate.
    //   // fpsControl.tick();

    //   // Draw the overlays.
    //   canvasCtx.save();
    //   canvasCtx.drawImage(results.image, 0, 0, canvasElement.width, canvasElement.height);
    //   if (!!results.objectDetections) {
    //     for (const detectedObject of results.objectDetections) {
    //       // Reformat keypoint information as landmarks, for easy drawing.
    //       console.log(detectedObject);
    //       // const landmarks:mpObjectron.Results =
    //       //     detectedObject.keypoints.map(x => x.point2d);
    //       // Draw bounding box.
    //       // drawingUtils.drawConnectors(canvasCtx, landmarks,
    //       //     mpObjectron.BOX_CONNECTIONS, {color: '#FF0000'});

    //       // Draw Axes
    //       // drawAxes(canvasCtx, landmarks, {
    //       //   x: '#00FF00',
    //       //   y: '#FF0000',
    //       //   z: '#0000FF',
    //       // });
    //       // Draw centroid.
    //       // drawingUtils.drawLandmarks(canvasCtx, [landmarks[0]], {color: '#FFFFFF'});
    //     }
    //   }
    //   canvasCtx.restore();
    // }

    const objectron = new Objectron({locateFile: (file) => {
      console.log("!!!!!!", file);
      return `./assets/objectron/${file}`;
      // return `https://cdn.jsdelivr.net/npm/@mediapipe/objectron/${file}`;
    }});

    objectron.setOptions({
      modelName: 'Cup',
      maxNumObjects: 3,
    });



    function onResults(results: ObjectronInterface) {
      // console.log("run result", results);
      canvasCtx.save();
      canvasCtx.drawImage(
          results.image, 0, 0, canvasElement.width, canvasElement.height);
      if (!!results.objectDetections) {
        for (const detectedObject of results.objectDetections) {
          console.log(detectedObject.keypoints);
          // Reformat keypoint information as landmarks, for easy drawing.
          // const landmarks: any =

          //     detectedObject.keypoints.map(x => x.point2d);
          const landmarks: mpObjectron.Point2D[] =
          detectedObject.keypoints.map(x => x.point2d);
          // // Draw bounding box.
          // Draw bounding box.
          drawingUtils.drawConnectors(canvasCtx, landmarks,
            mpObjectron.BOX_CONNECTIONS, {color: '#FF0000'});
          // // Draw centroid.
          drawingUtils.drawLandmarks(canvasCtx, [landmarks[0]], {color: '#FFFFFF'});
        }
      }
      canvasCtx.restore();
    }




    objectron.onResults(onResults);

    const camera = new Camera(videoElement, {
      onFrame: async () => {
        await objectron.send({image: videoElement});
      },
      width: 1280,
      height: 720
    });
    camera.start();
// ###############################################################





// ###############################################################
  return (
    <div className="App">
      {/* <div className="container">
        <video className="input_video"></video>
        <canvas className="output_canvas" width="1280px" height="720px"></canvas>
      </div> */}
    </div>
  );
}

export default App;
